export = React;
export as namespace React;

declare namespace React {
  type Component = (properties?: object) => object;

  const createElement: (
    ComponentOrTag: Component | string,
    properties: object,
    ...children: object[]
  ) => object;

  interface AriaAttributes {
    'aria-activedescendant'?: string | undefined;
    'aria-invalid'?: boolean | 'false' | 'true' | 'grammar' | 'spelling' | undefined;
  }
}

declare global {
  namespace JSX {
    interface IntrinsicElements {
      a: React.AriaAttributes;
      button: React.AriaAttributes;
      div: React.AriaAttributes;
      h1: React.AriaAttributes;
      label: React.AriaAttributes;
      main: React.AriaAttributes;
      span: React.AriaAttributes;
    }
  }
}
